import * as React from "react";
import { ethers } from "ethers";
import { useEffect, useState } from "react";
import { useForm } from "react-hook-form";
import abi from "./utils/WavePortal.json"
import './App.css';

const App = () =>{
	const { register, handleSubmit } = useForm();
	const [quote, setQuote] = useState("");

	const [currentAccount, setCurrentAccount] = useState("");

	const [waveReceived, setWaveReceived] = useState(false);

	const [allWaves, setAllWaves] = useState([]);



	const contractAddress = "0x7c8E8c3026f0021794A1DFb406EadAb858892b69";
	const etherscanContractAddress = "https://rinkeby.etherscan.io/address/".concat(contractAddress);
	const contractABI = abi.abi;


	const getAllWaves = async () => {
		try {
			const { ethereum } = window;
			console.log("entre dans la fonction getAllWaves");
			if (ethereum) {
				const provider = new ethers.providers.Web3Provider(ethereum);
				const signer = provider.getSigner();
				const wavePortalContract = new ethers.Contract(contractAddress, contractABI, signer);

				const waves = await wavePortalContract.getAllWaves();

				let wavesCleaned = [];
				waves.forEach(wave => {
					wavesCleaned.push({
						address: wave.waver,
						timestamp: new Date(wave.timestamp * 1000),
						message: wave.message
					});
				});

				setAllWaves(wavesCleaned);
			} else {
				console.log("Ethereum object doesn't exist!")
			}
		} catch (error) {
			console.log(error);
		}
	}

	//async -> allow to use await -> which enable promise-based bahavior to be written
	const checkIfWalletIsConnected = async () => {

		try {
			const { ethereum } = window;

			if (!ethereum) {
				console.log("Make sure you have metamask!");
				return;
			} else {
				console.log("We have the ethereum object", ethereum);
			}

/*
			Check if we're authorized to access to the user's wallet.
			We are looking for eth accounts.
			If the user have multiple account, it takes the first one.
			|	|	|	|	|	|	|	|	|	|	|	|	|	|	|	|	|	|
			v	v	v	v	v	v	v	v	v	v	v	v	v	v	v	v	v	v
*/
			const accounts = await ethereum.request({ method: "eth_accounts"});

			if (accounts.length !== 0) {
				const account = accounts[0]; //take the first account :)
				console.log("Found an authorized account:", account);
				setCurrentAccount(account);
				getAllWaves();
			} else {
				console.log("No authorized account found");
			}
		}

		catch (error) {
			console.log(error);
		}
	}

	const connectWallet = async () => {
		try {
			const { ethereum } = window;

			if (!ethereum) {
				alert("Get Metamask!");
				return;
			}

			const accounts = await ethereum.request({ method: "eth_requestAccounts" });

			console.log("Connected", accounts[0]);
			setCurrentAccount(accounts[0]);
		} catch (error){
			console.log(error);
		}
	}

	const wave = async () => {
		try {
			const { ethereum } = window;

			if (ethereum) {
				setWaveReceived(false);
				const provider = new ethers.providers.Web3Provider(ethereum);
				const signer = provider.getSigner();

				const wavePortalContract = new ethers.Contract(contractAddress, contractABI, signer);

				let count = await wavePortalContract.getTotalWaves();
				let countBackUp = count;
				console.log("Total waves: ", count.toNumber());

				//Execute the actual wave from the smart contract
				const waveTx = await wavePortalContract.wave("Mon message");

				console.log("Mining ...", waveTx.hash);

				await waveTx.wait();
				console.log("Mined -- ", waveTx.hash);

				count = await wavePortalContract.getTotalWaves();
				if (count != countBackUp)
				{
					console.log("Total waves: ", count.toNumber());
					setWaveReceived(true);
				}

			} else {
				console.log("Ethereum object doesn't exist!");

			}
		} catch (error) {
			console.log(error);
		}
		console.log("You have been waved");
	}

React.useEffect(() => {
	checkIfWalletIsConnected();
}, [])

  return (
    <div className="mainContainer">

      <div className="dataContainer">
        <div className="header">
        👋 Yo !
        </div>

        <div className="bio">
        Hey, écrit moi ta citation préférée :).<br /><br />
		<a href={etherscanContractAddress} target="_blank">Adresse du contrat</a><br />
		<a href="https://faucets.chain.link/rinkeby" target="_blank">Faucet</a>
		{(currentAccount) &&
		<><br/><br/>
		Ton adresse: {currentAccount}
		</>}
        </div>

		{/* If there is no currentAccount, it will render the - connect - button */}
		{(!currentAccount) && 
        <button className="waveButton" onClick={connectWallet}>
          Se connecter
        </button>
		}
		{/* If there is a currentAccount, it will render the - wave - button */}
		{/*(currentAccount) && 
        <button className="waveButton" onClick={wave}>
          Aboyer
        </button>*/
		}
		{(currentAccount) &&
		<form onSubmit={handleSubmit((quote) => setQuote(JSON.stringify(quote)))}>

			<input {...register("Ta citation")} placeholder="Ta citation" />
			<p>{quote}</p>
			<input type="submit" />
	  	</form>
		/*
		<form onSubmit={() => wave(quote)}>
			<label>Entrez votre citation<br/>
			<input type="text" value={quote} onChange={(quo)=>setQuote(quo.target.value)}/>
			<button type="submit">Submit</button>
			</label>
		</form>
		*/}
		{(currentAccount && waveReceived) &&
        <button className="received">
          🦁 Reçu!
        </button>
		}
		{allWaves.map((wave, index) => {
          return (
            <div key={index} style={{ backgroundColor: "OldLace", marginTop: "16px", padding: "8px" }}>
              <div>Address: {wave.address}</div>
              <div>Time: {wave.timestamp.toString()}</div>
              <div>Message: {wave.message}</div>
            </div>)
        })}
      </div>
    </div>
  );
}

export default App;
